components/common/social-links:
{{#if social_media}}
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank">
                    <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                </a>
            </li>
        {{/each}}
    </ul>
{{/if}}

components/psdc-common/payment-icons:
{{#with theme_settings}}
    {{#or show_accept_amex show_accept_discover show_accept_mastercard show_accept_paypal show_accept_visa show_accept_amazonpay show_accept_googlepay}}
        <div class="footer-payment-icons">
            {{#if show_accept_amex}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-american-express"></use></svg>
            {{/if}}
            {{#if show_accept_discover}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-discover"></use></svg>
            {{/if}}
            {{#if show_accept_mastercard}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-mastercard"></use></svg>
            {{/if}}
            {{#if show_accept_paypal}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-paypal"></use></svg>
            {{/if}}
            {{#if show_accept_visa}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-visa"></use></svg>
            {{/if}}
            {{#if show_accept_amazonpay}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-amazonpay"></use></svg>
            {{/if}}
            {{#if show_accept_googlepay}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-googlepay"></use></svg>
            {{/if}}
        </div>
    {{/or}}
{{/with}}

components/psdc-common/usp-section:
{{#if theme_settings.show_usp '!=' 0}}
  <div class="usp-section m-b-60 {{#if theme_settings.show_usp '==' 0}}usp-section-none{{/if}}">
    <div class="container">
      <ul>
        {{#if theme_settings.show_usp '>=' 1}}
          <li class="{{#if theme_settings.show_usp '>=' 1}}first{{/if}}">
            <div class="usp-block">

              {{#if theme_settings.selected-icon1 '!=' ''}}
                {{#if theme_settings.selected-icon1 '==' 'SelectOne'}}
                  {{#if theme_settings.UspImageFirst '!=' ''}}
                    <div class="usp-img">
                      <img src="/product_images/uploaded_images/{{theme_settings.UspImageFirst}}" alt="">
                    </div>
                  {{/if}}
                  {{else}}
                    <div class="usp-img">
                        <img src="{{cdn 'assets/img/usp-icon/'}}{{theme_settings.selected-icon1}}.png" alt="">
                    </div>
                {{/if}}
              {{/if}}

              <div class="usp-detail">
                {{#if theme_settings.UspTitleFirst}}
                  <h5>{{theme_settings.UspTitleFirst}}</h5>
                {{/if}}
                {{#if theme_settings.UspTextFirst}}
                  <p>{{theme_settings.UspTextFirst}}</p>
                {{/if}}
              </div>
            </div>
          </li>
        {{/if}}
        {{#if theme_settings.show_usp '>=' 2}}
          <li class="{{#if theme_settings.show_usp '>=' 2}}second{{/if}}">
            <div class="usp-block">

              {{#if theme_settings.selected-icon2 '!=' ''}}
                {{#if theme_settings.selected-icon2 '==' 'SelectOne'}}
