              <div class="usp-detail">
                {{#if theme_settings.UspTitleSecond}}
                  <h5>{{theme_settings.UspTitleSecond}}</h5>
                {{/if}}
                {{#if theme_settings.UspTextSecond}}
                  <p>{{theme_settings.UspTextSecond}}</p>
                {{/if}}
              </div>
            </div>
          </li>
        {{/if}}
        {{#if theme_settings.show_usp '>=' 3}}
        <li class="{{#if theme_settings.show_usp '>=' 3}}third{{/if}}">
          <div class="usp-block">

            {{#if theme_settings.selected-icon3 '!=' ''}}
