                <p>{{theme_settings.UspTextThird}}</p>
              {{/if}}
            </div>
          </div>
        </li>
        {{/if}}
        {{#if theme_settings.show_usp '>=' 4}}
          <li class="{{#if theme_settings.show_usp '>=' 4}}fourth{{/if}}">
            <div class="usp-block">

              {{#if theme_settings.selected-icon4 '!=' ''}}
                {{#if theme_settings.selected-icon4 '==' 'SelectOne'}}
                  {{#if theme_settings.UspImageFour '!=' ''}}
                    <div class="usp-img">
                      <img src="/product_images/uploaded_images/{{theme_settings.UspImageFour}}" alt="">
                    </div>
                  {{/if}}
